import React from "react";
import '../Navbar/nav.css'

function handleInputChange(event) {
  this.setState({
      searchTerm: event.target.value
  });
  let userTyped = event.target.value;
  const filteredList = this.props.employees.filter((item) => {
      let values = item.name.title + item.name.first + item.name.last + item.gender + item.dob.age + item.email + item.cell;
      return values.indexOf(userTyped) !== -1;

  });

  this.setState({
      filteredEmployees: filteredList

  });
}


function Navbar() {
  
 
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
      <div className="navbar-brand">Employee Directory</div>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <form className="d-flex">
            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={e => handleInputChange(e)}/>
            <button className="btn btn-outline-success" type="submit">Search</button>
          </form>
        </div>
      </div>
  </nav>
);
}

export default Navbar;
