import React from "react";

class Navbar extends React.Component {

    state = {
        search: "",
        searchedEmployees: []
    }

    handleInputChange = event => {
        this.setState({
            search: event.target.value
        });
        let userTyped = event.target.value;
        const filteredList = this.props.employees.filter((item) => {
            let values = item.name.title + item.name.first + item.name.last + item.gender + item.dob.age + item.email + item.cell;
            return values.indexOf(userTyped) !== -1;

        });

        this.setState({
            searchedEmployees: filteredList

        });
    }

    render () {
            return (

                <form className="d-flex">          
                    <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={event => this.handleInputChange(event)}/>
                    <button className="btn btn-outline-success" type="submit">Search</button>
                </form>

            )
        }
}

export default Navbar;
