import React, { Component } from "react";
import EmployeeCard from "../EmployeeCard/EmployeeCard"
import API from "../utils/API"

class EmployeeTable extends Component {

    state = {
        aToz: true,
        acending: true,
        employeeListing: [],
    }


    componentDidMount() {
        API.randomUser()
        .then(res => this.setState({ employeeListing: res.data.message }))
        .catch(err => console.log(err));
        }
    


    sortByName = () => {
        let sort = [];
        sort = this.props.empList.sort((a, b) => {
            let alphaA = a.name.last.toLowerCase();
            let alphaB = b.name.last.toLowerCase();
            if(alphaA > alphaB) {
                return -1
            } else if (alphaA < alphaB) {
                return 1 
            }else {
                return 0;
            }
            
         });  
    }

    render() {
        return (

            <div>
                <div className="header">
                    <div>Photo</div>
                    <div><p onClick={this.sortByName} className="name">Name</p> </div>
                    <div>Gender</div>
                    <div>Phone</div>
                    <div>E-mail</div>
                </div>

                {
                    this.state.employeeListing.length > 0 &&
                    this.state.employeeListing.map((item, index) => (

                            <EmployeeCard
                                image={item.picture.medium}
                                first={item.name.first}
                                last={item.name.last}
                                title={item.name.title}
                                gender={item.gender}
                                age={item.dob.age}
                                phone={item.cell}
                                email={item.email}
                            />
                    ))
                }
            </div>
        );
    }

}




export default EmployeeTable
